package chapter.five;

public class Test5_8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum1=0,sum2 = 0;
		for (int i = 0; i < 10; i++) {
			sum1 += i;
		}
		for (int i = 0; i < 10; ++i) {
			sum2 += i;
		}
		System.out.println(sum1);
		System.out.println(sum2);
	}

}
